function servidor_publico_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('servidor_publico');
	if(!$arrPerm['insert']) return false;

	$data = [
		'VcrIdSerP' => Request::val('VcrIdSerP', ''),
		'VcrSerPub' => Request::val('VcrSerPub', ''),
		'VcrTipSerP' => Request::val('VcrTipSerP', ''),
		'VcrIdProc' => Request::lookup('VcrIdProc', ''),
		'VcrCelSerP' => Request::val('VcrCelSerP', ''),
		'VcrCorrSerP' => Request::val('VcrCorrSerP', ''),
	];

	if($data['VcrIdSerP'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'DOCUMENTO IDENTIDAD SERVIDOR PUBLICO': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: servidor_publico_before_insert
	if(function_exists('servidor_publico_before_insert')) {
		$args = [];
		if(!servidor_publico_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('servidor_publico', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = $data['VcrIdSerP'];

	update_calc_fields('servidor_publico', $recID, calculated_fields()['servidor_publico']);

	// hook: servidor_publico_after_insert
	if(function_exists('servidor_publico_after_insert')) {
		$res = sql("SELECT * FROM `servidor_publico` WHERE `VcrIdSerP`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!servidor_publico_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('servidor_publico', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) servidor_publico_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

