function servidor_publico_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('servidor_publico', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: servidor_publico_before_delete
	if(function_exists('servidor_publico_before_delete')) {
		$args = [];
		if(!servidor_publico_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	// child table: historico_vt
	$res = sql("SELECT `VcrIdSerP` FROM `servidor_publico` WHERE `VcrIdSerP`='{$selected_id}'", $eo);
	$VcrIdSerP = db_fetch_row($res);
	$rires = sql("SELECT COUNT(1) FROM `historico_vt` WHERE `VcrIdSerP`='" . makeSafe($VcrIdSerP[0]) . "'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks) {
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace('<RelatedRecords>', $rirow[0], $RetMsg);
		$RetMsg = str_replace('<TableName>', 'historico_vt', $RetMsg);
		return $RetMsg;
	} elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks) {
		$RetMsg = $Translation['confirm delete'];
		$RetMsg = str_replace('<RelatedRecords>', $rirow[0], $RetMsg);
		$RetMsg = str_replace('<TableName>', 'historico_vt', $RetMsg);
		$RetMsg = str_replace('<Delete>', '<input type="button" class="btn btn-danger" value="' . html_attr($Translation['yes']) . '" onClick="window.location = \'servidor_publico_view.php?SelectedID=' . urlencode($selected_id) . '&delete_x=1&confirmed=1&csrf_token=' . urlencode(csrf_token(false, true)) . '\';">', $RetMsg);
		$RetMsg = str_replace('<Cancel>', '<input type="button" class="btn btn-success" value="' . html_attr($Translation[ 'no']) . '" onClick="window.location = \'servidor_publico_view.php?SelectedID=' . urlencode($selected_id) . '\';">', $RetMsg);
		return $RetMsg;
	}

	sql("DELETE FROM `servidor_publico` WHERE `VcrIdSerP`='{$selected_id}'", $eo);

	// hook: servidor_publico_after_delete
	if(function_exists('servidor_publico_after_delete')) {
		$args = [];
		servidor_publico_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='servidor_publico' AND `pkValue`='{$selected_id}'", $eo);
}

function servidor_publico_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('servidor_publico', $selected_id, 'edit')) return false;

	$data = [
		'VcrIdSerP' => Request::val('VcrIdSerP', ''),
		'VcrSerPub' => Request::val('VcrSerPub', ''),
		'VcrTipSerP' => Request::val('VcrTipSerP', ''),
		'VcrIdProc' => Request::lookup('VcrIdProc', ''),
		'VcrCelSerP' => Request::val('VcrCelSerP', ''),
		'VcrCorrSerP' => Request::val('VcrCorrSerP', ''),
	];

	if($data['VcrIdSerP'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'DOCUMENTO IDENTIDAD SERVIDOR PUBLICO': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('servidor_publico', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: servidor_publico_before_update
	if(function_exists('servidor_publico_before_update')) {
		$args = ['old_data' => $old_data];
		if(!servidor_publico_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'servidor_publico', 
		backtick_keys_once($set), 
		['`VcrIdSerP`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="servidor_publico_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}

	$data['selectedID'] = $data['VcrIdSerP'];
	$newID = $data['VcrIdSerP'];

	$eo = ['silentErrors' => true];

	update_calc_fields('servidor_publico', $data['selectedID'], calculated_fields()['servidor_publico']);

	// hook: servidor_publico_after_update
	if(function_exists('servidor_publico_after_update')) {
		$res = sql("SELECT * FROM `servidor_publico` WHERE `VcrIdSerP`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['VcrIdSerP'];
		$args = ['old_data' => $old_data];
		if(!servidor_publico_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "', `pkValue`='{$data['VcrIdSerP']}' WHERE `tableName`='servidor_publico' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);

	// if PK value changed, update $selected_id
	$selected_id = $newID;
}

