		$jsReadOnly .= "\tjQuery('#VcrSerPub').replaceWith('<div class=\"form-control-static\" id=\"VcrSerPub\">' + (jQuery('#VcrSerPub').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#VcrTipSerP').replaceWith('<div class=\"form-control-static\" id=\"VcrTipSerP\">' + (jQuery('#VcrTipSerP').val() || '') + '</div>'); jQuery('#VcrTipSerP-multi-selection-help').hide();\n";
		$jsReadOnly .= "\tjQuery('#VcrIdProc').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#VcrIdProc_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#VcrCelSerP').replaceWith('<div class=\"form-control-static\" id=\"VcrCelSerP\">' + (jQuery('#VcrCelSerP').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#VcrCorrSerP').replaceWith('<div class=\"form-control-static\" id=\"VcrCorrSerP\">' + (jQuery('#VcrCorrSerP').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#VcrCorrSerP, #VcrCorrSerP-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(VcrTipSerP)%%>', $combo_VcrTipSerP->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(VcrTipSerP)%%>', $combo_VcrTipSerP->SelectedData, $templateCode);
	$templateCode = str_replace('<%%COMBO(VcrIdProc)%%>', $combo_VcrIdProc->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(VcrIdProc)%%>', $combo_VcrIdProc->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(VcrIdProc)%%>', urlencode($combo_VcrIdProc->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['VcrIdProc' => ['procesos', 'Proceso asociado'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(VcrIdSerP)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(VcrSerPub)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(VcrTipSerP)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(VcrIdProc)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(VcrCelSerP)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(VcrCorrSerP)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(VcrIdSerP)%%>', safe_html($urow['VcrIdSerP']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(VcrIdSerP)%%>', html_attr($row['VcrIdSerP']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VcrIdSerP)%%>', urlencode($urow['VcrIdSerP']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(VcrSerPub)%%>', safe_html($urow['VcrSerPub']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(VcrSerPub)%%>', html_attr($row['VcrSerPub']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VcrSerPub)%%>', urlencode($urow['VcrSerPub']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(VcrTipSerP)%%>', safe_html($urow['VcrTipSerP']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(VcrTipSerP)%%>', html_attr($row['VcrTipSerP']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VcrTipSerP)%%>', urlencode($urow['VcrTipSerP']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(VcrIdProc)%%>', safe_html($urow['VcrIdProc']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(VcrIdProc)%%>', html_attr($row['VcrIdProc']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VcrIdProc)%%>', urlencode($urow['VcrIdProc']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(VcrCelSerP)%%>', safe_html($urow['VcrCelSerP']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(VcrCelSerP)%%>', html_attr($row['VcrCelSerP']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VcrCelSerP)%%>', urlencode($urow['VcrCelSerP']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(VcrCorrSerP)%%>', safe_html($urow['VcrCorrSerP']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(VcrCorrSerP)%%>', html_attr($row['VcrCorrSerP']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VcrCorrSerP)%%>', urlencode($urow['VcrCorrSerP']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(VcrIdSerP)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VcrIdSerP)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(VcrSerPub)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VcrSerPub)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(VcrTipSerP)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VcrTipSerP)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(VcrIdProc)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VcrIdProc)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(VcrCelSerP)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VcrCelSerP)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(VcrCorrSerP)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VcrCorrSerP)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('VcrCorrSerPEdit')) { document.getElementById('VcrCorrSerPEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('VcrCorrSerPEditLink')) { document.getElementById('VcrCorrSerPEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('servidor_publico');
	if($selected_id) {
		$jdata = get_joined_record('servidor_publico', $selected_id);
		if($jdata === false) $jdata = get_defaults('servidor_publico');
		$rdata = $row;
	}
	$templateCode .= loadView('servidor_publico-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: servidor_publico_dv
	if(function_exists('servidor_publico_dv')) {
		$args = [];
		servidor_publico_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}